css:
@background-color-selected :  #2a2139// rgba(255,255,255,0.21176470588235294);
/*
Other ui styels less template from the web
// This file has fallback variables. It specifies the variables themes
// must implement.

// Colors

@text-color: #333;
@text-color-subtle: #777;
@text-color-highlight: #111;
@text-color-selected: @text-color-highlight;

@text-color-info: #5293d8;
@text-color-success: #1fe977;
@text-color-warning: #f78a46;
@text-color-error: #c00;

@background-color-info: #0098ff;
@background-color-success: #17ca65;
@background-color-warning: #ff4800;
@background-color-error: #c00;
@background-color-highlight: hsla(0,0%,0%,.1);
@background-color-selected: @background-color-highlight;

@app-background-color: #fff;

@base-background-color: #fff;
@base-border-color: #eee;

@pane-item-background-color: @base-background-color;
@pane-item-border-color: @base-border-color;

@input-background-color: #fff;
@input-border-color: @base-border-color;

@tool-panel-background-color: #f4f4f4;
@tool-panel-border-color: @base-border-color;

@inset-panel-background-color: #eee;
@inset-panel-border-color: @base-border-color;

@panel-heading-background-color: #ddd;
@panel-heading-border-color: transparent;

@overlay-background-color: #f4f4f4;
@overlay-border-color: @base-border-color;

@button-background-color: #ccc;
@button-background-color-hover: lighten(@button-background-color, 5%);
@button-background-color-selected: @button-background-color-hover;
@button-border-color: #aaa;

@tab-bar-background-color: #fff;
@tab-bar-border-color: darken(@tab-background-color-active, 10%);
@tab-background-color: #f4f4f4;
@tab-background-color-active: #fff;
@tab-border-color: @base-border-color;

@tree-view-background-color: @tool-panel-background-color;
@tree-view-border-color: @tool-panel-border-color;

@ui-site-color-1: @background-color-success; // green
@ui-site-color-2: @background-color-info; // blue
@ui-site-color-3: @background-color-warning; // orange
@ui-site-color-4: #db2ff4; // purple
@ui-site-color-5: #f5e11d; // yellow


// Sizes

@font-size: 13px;
@input-font-size: 14px;

@disclosure-arrow-size: 12px;

@component-padding: 10px;
@component-icon-padding: 5px;
@component-icon-size: 16px;
@component-line-height: 25px;
@component-border-radius: 2px;

@tab-height: 30px;

// Other

@font-family: system-ui;
@use-custom-controls: true; // false uses native controls
*/
.settings-view .package-card {
  padding: 15px;
  margin-bottom: 10px;
  list-style-type: none;
  font-size: 1.2em;
  border-radius: 4px;
  border: 1px solid #100c15;
  background-color: #382a49;
  overflow: hidden;
  cursor: pointer;
  -webkit-user-select: none;
}
.settings-view .package-card:hover {
  background-color: #3d2e4f;
}
.settings-view .package-card:active {
  background-color: #382a49;
}
.settings-view .package-card.disabled {
  background-color: #30243f;
}
.settings-view .package-card.disabled .body,
.settings-view .package-card.disabled .avatar,
.settings-view .package-card.disabled .author,
.settings-view .package-card.disabled .stats {
  opacity: .5;
}
.settings-view .package-card.deprecated {
  background-color: #30243f;
}
.settings-view .package-card.deprecated .avatar,
.settings-view .package-card.deprecated .author,
.settings-view .package-card.deprecated .stats {
  opacity: .5;
}
.settings-view .package-card .deprecation-badge {
  display: none;
  font-size: .75em;
  text-transform: uppercase;
}
.settings-view .package-card.deprecated .deprecation-badge {
  display: inline;
}
.settings-view .package-card.col-lg-4 {
  min-width: 190px;
  padding: 10px 0;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .package-card .thumbnail {
  border-radius: 3px;
  margin: 0;
  min-height: 140px;
  color: #a8a8a8;
  border-radius: 4px;
  border: 1px solid #100c15;
  background-color: #382a49;
}
.settings-view .package-card .thumbnail .caption {
  width: 100%;
  position: relative;
}
.settings-view .package-card .package-name {
  margin-right: .5em;
  font-weight: bolder;
  color: #f0f0f0;
}
.settings-view .package-card .package-version {
  font-size: .8em;
  margin-right: 10px;
}
.settings-view .package-card .description {
  color: #a8a8a8;
  overflow: hidden;
  min-height: 38px;
  max-height: 38px;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .package-card .package-message p:last-child {
  margin: 0;
}
.settings-view .package-card .downloads {
  color: #f0f0f0;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .package-card .package-status {
  right: 0px;
  top: 5px;
  text-align: center;
}
.settings-view .package-card .package-status.icon-cloud-download {
  color: #428bca;
}
.settings-view .package-card .package-status.icon-check {
  color: #6CC644;
}
.settings-view .package-card .package-status.icon-alert {
  color: #BD2C00;
}
.settings-view .package-card .card-name {
  font-weight: 300;
  margin: 0 0 .2em 0;
  font-size: 1.2em;
  line-height: 1.4;
}
.settings-view .package-card .card-name .css-truncate-target {
  color: $greenDark;
}
.settings-view .package-card .stats .stats-item {
  margin-left: 15px;
  height: 26px;
  display: inline-block;
  line-height: 24px;
  vertical-align: top;
}
.settings-view .package-card .stats .stats-item .icon {
  color: #808080;
}
.settings-view .package-card .stats .star-box {
  display: inline-block;
  vertical-align: top;
}
.settings-view .package-card .stats .star-box .star-button {
  padding: 0 6px;
}
.settings-view .package-card .stats .star-box .star-button .octicon {
  margin-right: 0px;
}
.settings-view .package-card .body .css-truncate-target {
  max-width: 100%;
}
.settings-view .package-card .package-description {
  white-space: normal;
  line-height: 1.4;
}
.settings-view .package-card .meta {
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-end;
  width: 100%;
}
.settings-view .package-card .meta a.linked-octicon {
  text-decoration: none;
}
.settings-view .package-card .meta a.linked-octicon:hover .octicon {
  color: $greenDark;
}
.settings-view .package-card .meta a.linked-octicon:hover .value {
  color: $greenDark;
}
.settings-view .package-card .meta .action {
  border-radius: 3px;
  padding: 3px 6px;
}
.settings-view .package-card .meta .action:hover {
  background-color: #eaeaea;
}
.settings-view .package-card .meta .value {
  color: #999;
}
.settings-view .package-card .meta-user {
  flex: 1 0 auto;
  margin-top: 10px;
  margin-right: 10px;
}
.settings-view .package-card .meta-user .avatar {
  border-radius: 2px;
  height: 24px;
  width: 24px;
  display: inline-block;
  vertical-align: middle;
  background-color: rgba(0, 0, 0, 0.1);
}
.settings-view .package-card .meta-user .author {
  color: #808080;
  margin-left: 10px;
}
.settings-view .package-card .meta-user .author:focus {
  outline: none;
}
.settings-view .package-card .meta-controls {
  margin-top: 5px;
}
.settings-view .package-card .meta-controls .install-button.is-installing,
.settings-view .package-card .meta-controls .uninstall-button.is-uninstalling {
  opacity: 1;
  background-image: linear-gradient(to right, transparent 50%, rgba(0, 0, 0, 0.15) 50%);
  background-size: 10px 100%;
  -webkit-animation: available-package-is-installing 0.5s linear infinite;
}
.settings-view .package-card .meta-controls .status-indicator {
  padding: 0;
  width: 5px;
  min-width: 4px;
  pointer-events: none;
  transition: background .4s;
  background: #1c9c71;
}
.settings-view .package-card .meta-controls .status-indicator.is-disabled {
  background: #cc8533;
}
.settings-view .package-card .meta-controls .btn-toolbar > .btn-group {
  float: left;
  margin: 5px 0 0 5px;
}
.settings-view .package-card .meta-lower {
  padding: 13px;
  background-color: #18121f;
  line-height: 1;
}
.settings-view .package-card.blank-slate {
  text-align: center;
  color: $beigeDark;
  height: 132px;
  line-height: 132px;
}
.settings-view .package-detail .package-card {
  cursor: default;
}
.settings-view .package-detail .package-card:hover {
  background-color: #382a49;
}
.settings-view .package-detail .package-card.disabled {
  background-color: #30243f;
}
@-webkit-keyframes available-package-is-installing {
  100% {
    background-position: -10px 0px;
  }
}
