}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #d49854;
}
.btn.btn-error {
  color: #ffffff;
  background-color: #d92626;
}
.btn.btn-error:hover,
.btn.btn-error:focus {
  background-color: #dc3838;
}
.btn.btn-error:active {
  background-color: #c72323;
}
.btn.btn-error.selected {
  background-color: #dc3838;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #df4949;
}
.btn-group > .btn {
  border-color: rgba(0, 0, 0, 0.2);
}
atom-text-editor[mini] {
  padding-left: 5px;
  color: #a8a8a8;
  border-radius: 2px;
  background-color: #18121f;
}
atom-text-editor[mini].is-focused {
  background-color: #0b080f;
}
atom-text-editor[mini] .placeholder-text {
  color: #808080;
}
atom-text-editor[mini] .selection .region {
  background-color: #30243f;
}
.find-and-replace {
  border-top: 1px solid #100c15;
}
.status {
  color: #a8a8a8;
}
.status-added {
  color: rgba(114, 240, 184, 0.8);
}
.status-ignored {
  color: rgba(255, 255, 255, 0.34901961);
}
.status-modified {
  color: rgba(183, 147, 206, 0.93333333);
}
.status-removed {
  color: #fe4450;
}
.status-renamed {
  color: #72f1b8;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 5px;
  line-height: 2;
  font-family: 'Helvetica Neue', Arial, sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 2px;
  background-color: #18121f;
}
.list-group li {
  padding-left: 5px;
}
.list-group li.selected {
  color: #ffffff;
  background-color: #30243f;
}
.list-group li.selected:before {
  display: none;
}
.list-tree {
  color: #a8a8a8;
}
.list-tree .selected {
  color: #ffffff;
}
.select-list li {
  padding: 5px 10px;
}
.select-list .status {
  float: right;
}
.select-list.popover-list {
  padding: 5px;
  border-radius: 2px;
  background-color: #30243f;
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.select-list.popover-list .list-group {
  margin-top: 5px;
}
.select-list.popover-list .list-group li {
  padding-left: 5px;
}
.panel-heading {
  border-bottom: 1px solid #100c15;
  background-color: #2e223c;
}
.inset-panel {
  background-color: #2e223c;
}
.inset-panel .panel-heading {
  background-color: #382a49;
}
atom-panel.modal {
  border: 1px solid #100c15;
  border-top: none;
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
