  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.ui-site-1 {
  background-color: #0088ff;
}
.ui-site-2 {
  background-color: #20b684;
}
.ui-site-3 {
  background-color: #cc8533;
}
.ui-site-4 {
  background-color: #D831B0;
}
.ui-site-5 {
  background-color: #EBDD5B;
}
.status-bar {
  font-size: 12px;
  color: #a8a8a8;
  border-top: 1px solid #100c15;
  background-color: #241b2f;
}
.status-bar:before {
  content: '';
  position: absolute;
  top: -1px;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(to right, #fc28a8, #03edf9) !important;
  opacity: .5;
  z-index: 6;
}
.tab-bar {
  background-color: #241b2f;
  border-bottom: 1px solid #100c15;
}
.tab-bar .tab {
  color: #a8a8a8;
  height: 30px;
  line-height: 30px;
  font-size: 12px;
  border-right: 1px solid #241b2f;
  background-color: #241b2f;
}
.tab-bar .tab.active {
  color: #ffffff;
  background-color: #241b2f;
}
.tab-bar .tab.texteditor.active {
  box-shadow: inset 0 -5px 25px rgba(255, 34, 165, 0.14509804);
  position: relative;
}
.tab-bar .tab.texteditor.active:after {
  content: '';
  position: absolute;
  bottom: -1px;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(270deg, #fc28a8, #03edf9);
  background-size: 150% 150%;
  -webkit-animation: AnimationName 10s ease infinite;
  -moz-animation: AnimationName 10s ease infinite;
  animation: AnimationName 10s ease infinite;
  opacity: 1;
  z-index: 6;
}
@-webkit-keyframes AnimationName {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@-moz-keyframes AnimationName {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
@keyframes AnimationName {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 50%;
  right: 14px;
  margin-top: -3px;
  border-color: rgba(255, 255, 255, 0.5);
  background-color: rgba(255, 255, 255, 0.5);
  border: none;
  background: linear-gradient(to bottom, #fff951 22%, #fc28a8);
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon:hover {
  color: #ffffff;
}
.tab-bar .tab.active .close-icon:hover {
  color: #a8a8a8;
}
.tab-bar .tab.is-dragging {
  background: #0b080f;
  border-color: transparent;
  opacity: .5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
